        for country_data in countries_data:
            currency = None
            if country_data['currency_code']:
                try:
                    currency = Currency.objects.get(code=country_data['currency_code'])
                except Currency.DoesNotExist:
                    self.stdout.write(
                        self.style.WARNING(
                            f"Currency {country_data['currency_code']} not found for {country_data['name']}"
                        )
                    )

            country, created = Country.objects.update_or_create(
                code=country_data['code'],
                defaults={
                    'name': country_data['name'],
                    'flag_emoji': country_data['flag_emoji'],
                    'phone_code': country_data['phone_code'],
                    'currency': currency,
                    'is_active': True
                }
            )

            if created:
                created_count += 1
            else:
                updated_count += 1

        self.stdout.write(
            self.style.SUCCESS(
                f'Successfully populated countries: {created_count} created, {updated_count} updated'
            )
        )
